import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import amber from '@material-ui/core/colors/amber';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import YouTubeIcon from '@material-ui/icons/YouTube';
import GitHubIcon from '@material-ui/icons/GitHub';
import FacebookIcon from '@material-ui/icons/Facebook';
import HomePage from "./HomePage";
import HobbiesPage from "./HobbiesPage";
import ProjectsPage from "./ProjectsPage";
import ResumePage from "./ResumePage";

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <Typography
            component="div"
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            {value === index && <Box p={3}>{children}</Box>}
        </Typography>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}

function a11yPropsNav(index) {
    return {
        id: `nav-tab-${index}`,
        'aria-controls': `nav-tabpanel-${index}`,
    };
}

function LinkTab(props) {
    return (
        <Tab
            component="a"
            onClick={event => {
                event.preventDefault();
            }}
            {...props}
        />
    );
}

const nameMargin = 0;
const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        //backgroundColor: theme.palette.background.paper,
        background: amber[50],
        display: 'flex',
        height: 224*4.3,
    },
    tabs: {
        borderRight: `5px solid ${theme.palette.divider}`,
        background: amber[50],
    },
    tab: {
        textTransform: 'none',
        background: 'linear-gradient(90deg, #FFF176 30%, #FFE082 90%)',
        minWidth: 270 + 2*nameMargin,
        borderRadius: 7,
        width: 270,
        minHeight: 100,
        height:  100,
        marginBottom: 5,
        marginLeft: 30,
        fontSize: 17,
        fontStyle: 'italic',
        fontWeight: 'bold',
        fontFamily: "Garamond",
    },
    nameTab: {
        textTransform: 'none',
        background: 'linear-gradient(90deg, #FFF176 30%, #FFE082 90%)',
        minWidth: 300,
        width: 300,
        //borderRadius: 20,
        minHeight: 150,
        height:  150,
        margin: nameMargin,
        marginBottom: 15,
        fontSize: 27,
        fontStyle: 'italic',
        //fontWeight: 'bold',
        fontFamily: 'Garamond'
    }
}));

export default function VerticalTabs() {
    const classes = useStyles();
    const [value, setValue] = React.useState(1);
    const resumepath = '../images/Resume_Latest.pdf';
    const resumetype = 'pdf';
    const tabs = ['Home', 'Hobbies and Interest', 'Personal Projects', 'Resume / CV'];
    const tabs_content = [<HomePage/>, <HobbiesPage/>, <ProjectsPage/>, <ResumePage/>];
    const links_labels = ['Github', 'LinkedIn', 'Facebook', 'Youtube'];
    const links_icons = [<GitHubIcon/>, <LinkedInIcon/>, <FacebookIcon/>, <YouTubeIcon/>]
    const links_url = ['https://github.com/arjunramesh99', 'https://github.com/arjunramesh99', 'https://github.com/arjunramesh99', 'https://github.com/arjunramesh99'];


    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <div className={classes.root}>
            <Tabs
                orientation="vertical"
                variant="scrollable"
                value={value}
                onChange={handleChange}
                aria-label="Vertical tabs example"
                className={classes.tabs}
            >

                {/* Render Tabs */}
                <Tab className={classes.nameTab} disabled={true} label="Arjun Ramesh" {...a11yProps(0)}/>
                {tabs.map((name, index) =>
                    <Tab className={classes.tab} label={name} {...a11yProps({index})}/>
                )}
                <ButtonGroup
                    orientation="vertical"
                    color="primary"
                    aria-label="vertical outlined primary button group"
                >
                    <Button>One</Button>
                    <Button>Two</Button>
                    <Button>Three</Button>
                </ButtonGroup>
                {/* Render Links */}
                <Button
                    variant="contained"
                    color="secondary"
                    className={classes.button}
                    startIcon={<DeleteIcon />}
                >
                    Delete
                </Button>
                {links_labels.map((name, index) =>
                    <LinkTab className={classes.tab} href="https://github.com/arjunramesh99" icon={links_icons[index]} {...a11yPropsNav({index})} />
                )}
            </Tabs>

            {tabs_content.map((page_component, index) =>
                <TabPanel value={value} index={index + 1}> {page_component} </TabPanel>
            )}
        </div>
    );
}
