import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import resume from '../images/Resume.jpg';


function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <Typography
            component="div"
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            {value === index && <Box p={3}>{children}</Box>}
        </Typography>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.paper,
        display: 'flex',
        height: 224*5,
    },
    tabs: {
        borderRight: `5px solid ${theme.palette.divider}`,
    },
    tab: {
        background: 'linear-gradient(90deg, #FFFDE7 30%, #FFFDE7 90%)',
        borderBottomColor: 'red',
        borderRadius: 10,
        minWidth: 300,
        width: 300,
        minHeight: 100,
        height:  100,
        marginBottom: 2,
        fontSize: 15
    }
}));

export default function VerticalTabs() {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <div className={classes.root}>
            <Tabs
                orientation="vertical"
                variant="scrollable"
                value={value}
                onChange={handleChange}
                aria-label="Vertical tabs example"
                className={classes.tabs}
            >
                <Tab className={classes.tab} label="Home" {...a11yProps(0)} />
                <Tab className={classes.tab} label="Hobbies and Interests" {...a11yProps(1)} />
                <Tab className={classes.tab} label="Personal Projects" {...a11yProps(2)} />
                <Tab className={classes.tab} label="Work Experience" {...a11yProps(3)} />
            </Tabs>
            <TabPanel value={value} index={0}>
               <img src={resume} alt="resume"/>
            </TabPanel>
            <TabPanel value={value} index={1}>
                Hobbies Page
            </TabPanel>
            <TabPanel value={value} index={2}>
                Projects Page
            </TabPanel>
            <TabPanel value={value} index={3}>
                Work Experience Page
            </TabPanel>
        </div>
    );
}
