import React, {useState} from 'react';
import ReactContactForm from 'react-mail-form';
import makeStyles from "@material-ui/core/styles/makeStyles";
import TextField from "@material-ui/core/TextField";
import DP from "./images/Home/DP.jpg"
import InfoCard from "./utlilty/InfoCard";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import MailOutlineIcon from '@material-ui/icons/MailOutline';

const useStyles = makeStyles(theme => ({
    contact: {
        minWidth: 1000,
        minHeight: 1000,
    },
    dp: {
        width: "100%",
        height: "100%",
        borderRadius: 50,
    },
    home: {
        padding: 20,
    },
    bio: {
        padding: 20,
        paddingLeft: 50,
        paddingRight: 50
    },
    connect: {
        fontSize: 28,
        fontFamily: "Garamond",
        fontWeight: 'bold',
        textAlign: 'center',
    },
    connect_card: {
        padding: 30,
        background: 'white'
    },
    name: {
        marginBottom: 20,
        fontSize: 28,
        fontWeight: 'bold'
    },
    input: {
        marginTop: 20,
        marginLeft: 40,
        minWidth: 300,
        width: 300,
    },
    input2: {
        marginTop: 20,
        marginLeft: 40,
        minWidth: 400,
        width: 450,
    },
    send_button: {
        marginTop: 20,
        marginLeft: '40%',
    }
}));

export default function HomePage() {
    const classes = useStyles();
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSendClick = () => {
        console.log('Clicked');
        {/* Reset fields */}
        setEmail('');
        setMessage('');
        console.log(email);
    }

    return (
        <Grid container spacing={5} direction="row-reverse" justify="center" className={classes.home}>
            <Grid item xs={7}>
                <div>
                    <Typography align='center' className={classes.name}>
                        Arjun Ramesh
                    </Typography>
                    <InfoCard title="Bio" className={classes.bio}>
                        An essay is, generally, a piece of writing that gives the author's own argument — but the
                        definition
                        is vague, overlapping with those of a paper, an article, a pamphlet, and a short story. Essays
                        have
                        traditionally been sub-classified as formal and informal. Formal essays are characterized by
                        "serious purpose, dignity, logical organization, length," whereas the informal essay is
                        characterized by "the personal element (self-revelation, individual tastes and experiences,
                        confidential manner), humor, graceful style, rambling structure, unconventionality or novelty of
                        theme," etc.[1]

                        Essays are commonly used as literary criticism, political manifestos, learned arguments,
                        observations of daily life, recollections, and reflections of the author. Almost all modern
                        essays
                        are written in prose, but works in verse have been dubbed essays (e.g., Alexander Pope's An
                        Essay on
                        Criticism and An Essay on Man). While brevity usually defines an essay, voluminous works like
                        John
                        Locke's An Essay Concerning Human Understanding and Thomas Malthus's An Essay on the Principle
                        of
                        Population are counterexamples.
                    </InfoCard>
                </div>
            </Grid>
            <Grid item xs={4}>
                <img className={classes.dp} src={DP} alt="Display pic"/>
            </Grid>
            <Grid item xs={5}>
                <Card raised className={classes.connect_card}>
                    <div className={classes.connect}>
                        Connect with me!
                    </div>
                    <div>
                        <TextField className={classes.input}
                                   id="dsfq"
                                   label="Email Address"
                                   variant="outlined"
                                   placeholder="abc@example.com"
                                   required
                                   size="small"
                                   onChange={(event) => setEmail(event.target.value)}
                        />
                    </div>
                    <div>
                        <TextField className={classes.input2}
                                   id="dsfq"
                                   label="Message"
                                   variant="outlined"
                                   multiline
                                   rows={5}
                                   placeholder="My name is abc, and I want to connect with you!"
                                   size="small"
                                   onChange={(event) => setMessage(event.target.value)}
                        />
                    </div>
                    <Button className={classes.send_button}
                            variant="contained"
                            endIcon={<MailOutlineIcon/>}
                            onClick={handleSendClick}
                    >
                        Send
                    </Button>
                </Card>

                {/*<InfoCard title="Connect with me!"/> */}
            </Grid>
        </Grid>

    );
}
