import React from 'react';
import amber from '@material-ui/core/colors/amber';
import grey from '@material-ui/core/colors/grey';
import Card from '@material-ui/core/Card';
import CardActions from "@material-ui/core/CardActions";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Grid from "@material-ui/core/Grid";
import makeStyles from "@material-ui/core/styles/makeStyles";
import {FlippingCard, FlippingCardBack, FlippingCardFront} from 'react-ui-cards'

const DP_width = 355;
const DP_height = DP_width;

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1
    },
    card: {
        position: 'relative',
        minHeight: 30,
        height: 355,
        background: 'black',
        //grey[900]//'linear-gradient(45deg, #FFECB3, #FFE082)' //amber[100]
    },
    flip_card: {
        minHeight: 550,
        height: 850,
        minWidth: 550,
        width: 850,
    },
    link_bar: {
        //minWidth: 700,
        backgroundColor: amber[300],
        minHeight: 40,
        height: 40,
        position: 'relative'
    },
    media: {
        minWidth: DP_width,
        width: DP_width,
        minHeight: DP_height,
        height: DP_height,
        opacity: 0.4
    },
    title: {
        position: 'absolute',
        fontSize: 44,
        color: amber[50],
        fontWeight: 'bold',
        fontFamily: 'Garamond',
        margin: 'auto',
        top: "45%",
        left: "20%",
    }
}))

export default function HobbiesPage() {
    const classes = useStyles();
    return (
        <div>
            <Grid container justify="space-evenly" spacing={7}>
                {[...Array(6)].map((a, b) =>
                    <Grid item xs={3}>
                        <Card raised className={classes.card}>
                            {/*<CardHeader title={"SpeedCubing"}/>*/}
                            <CardMedia className={classes.media}
                                       title="SpeedCubing"
                                       image={require("../images/Cubing/Cubing_DP.jpg")}
                            />
                            <div className={classes.title}>
                                Speedcubing
                            </div>
                            {/*<CardContent>
                            Hello
                        </CardContent>*/}

                        </Card>
                        <Card raised className={classes.link_bar}>
                            <CardContent>
                                Tags
                            </CardContent>
                        </Card>
                    </Grid>
                )}
            </Grid>
        </div>
    );
}
